import { useEffect, useRef, useState } from 'react';
import 'react-toastify/dist/ReactToastify.css';
import { Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/Header'
import ProductList from './components/ProductList'
import { getProducts, addProduct} from './api/ProductService';
import { toastError } from './api/ToastService';
import { ToastContainer } from 'react-toastify';

function App() {
     const modalRef = useRef();
     const [data, setData] = useState({});
     const [currentPage, setCurrentPage] = useState(0);
     const [values, setValues] = useState({
         name: '',
         category: '',
         description: '',
         price: '',
         imageUrl: ''
     });

     const getAllProducts = async (page = 0, size = 10) => {
        try {
          setCurrentPage(page);
          const { data } = await getProducts(page, size);
          setData(data);
          console.log("data retrieved");
          console.log(data);
        } catch(error) {
          console.log(error);
        }
     }

     const handleNewProduct = async (event) => {
          event.preventDefault();
          try {
                const { data } = await addProduct(values);
                toggleModal(false);
                setValues({
                  name: '',
                  category: '',
                  description: '',
                  price: '',
                  imageUrl: ''
                })
                getAllProducts();
          } catch (error) {
                console.log(error);
                toastError(error.message);
          }
    };

  useEffect(() => {
    getAllProducts();
  }, []);

  const toggleModal = show => show ? modalRef.current.showModal() : modalRef.current.close();

  return (
    <>
      <Header toggleModal={toggleModal}  numberOfProducts={data.totalElements} />
      <main className='main'>
        <div className='container'>
          <Routes>
            <Route path="/" element={<ProductList data={data} currentPage={currentPage} getAllProducts={getAllProducts} />} />
          </Routes>
        </div>
      </main>
      <ToastContainer />
      </>
  );
}

