import React from 'react';
import Product from "./Product"
import { SearchBar } from "./SearchBar";

const SearchProduct = ({ data, searchProducts, currentPage}) => {
    const [results, setResults] = useState([]);

    return (
        <main className='main'>
            <div className="product__search_bar">
                <SearchBar searchProducts={searchProducts} setResults={setResults} />
                <div>SerachResult</div>
                </div>
            <ul className='product__list'>
                {setResults?.length > 0 && setResults.map(product => <Product product={product} key={product.id} />)}
            </ul>

            {data?.length > 0 && data?.length > 1 &&
            <div className='pagination'>
                <a onClick={() => searchProducts(currentPage - 1)} className={0 === currentPage ? 'disabled' : ''}>&laquo;</a>

                { data && [...Array(data.totalPages).keys()].map((page, index) => 
                    <a onClick={() => searchProducts(page)} className={currentPage === page ? 'active' : ''} key={page}>{page + 1}</a>)}


                <a onClick={() => searchProducts(currentPage + 1)} className={data.length === currentPage + 1 ? 'disabled' : ''}>&raquo;</a>
            </div>            
            }

        </main>
    )
}

export default SearchProduct
