import React, { useState, useEffect, useRef } from 'react';
import { Link, useParams } from 'react-router-dom';
import { getProduct } from '../api/ProductService';
import { toastError, toastSuccess } from '../api/ToastService';

const ProductDetail = ({ updateProduct }) => {
    const inputRef = useRef();
    const [product, setProduct] = useState({
        id: '',
        name: '',
        category: '',
        description: '',
        price: '',
        imageUrl: ''
    });

    const { id } = useParams();

    const fetchProduct = async (id) => {
        try {
            const { data } = await getProduct(id);
            setProduct(data);
            console.log(data);
        } catch (error) {
            console.log(error);
            toastError(error.message);
        }
    };

    const selectImage = () => {
        inputRef.current.click();
    };

    useEffect(() => {
        fetchContact(id);
    }, []);

    return (
        <>
            <Link to={'/products'} className='link'><i className='bi bi-arrow-left'></i> Back to list</Link>
            <div className='profile'>
                <div className='profile__details'>
                    <img src={product.imageUrl} alt={`Product photo of ${product.name}`} />
                    <div className='profile__metadata'>
                        <p className='profile__name'>{product.name}</p>
                        <p className='profile__muted'>JPG, GIF, or PNG. Max size of 10MG</p>
                    </div>
                </div>
                <div className='profile__settings'>
                    <div>
                        <form onSubmit={onUpdateContact} className="form">
                            <div className="user-details">
                                <input type="hidden" defaultValue={product.id} name="id" required />
                                <div className="input-box">
                                    <span className="details">Category</span>
                                    <input type="text" value={product.category} onChange={onChange} name="name" required />
                                </div>
                            </div>
                            <div className="form_footer">
                                <button type="submit" className="btn">Save</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </>
    )
}

export default ProductDetail;
