import React from 'react';
import Product from "./Product"

const ProductList = ({ data, currentPage, getAllProducts }) => {
    console.log("inside product list...");
    console.log("data here is : " + data.content);
    console.log("data length here is : " + data?.content?.length);
    return (
        <main className='main'>
            {data?.length === 0 && <div>No Products. Please add a new product</div>}

            <ul className='product__list'>
                {data?.length > 0 && data.map(product => <Product product={product} key={product.id} />)}
            </ul>

            {data?.length > 0 && data?.length > 1 &&
            <div className='pagination'>
                <a onClick={() => getAllProducts(currentPage - 1)} className={0 === currentPage ? 'disabled' : ''}>&laquo;</a>

                { data && [...Array(data.totalPages).keys()].map((page, index) => 
                    <a onClick={() => getAllProducts(page)} className={currentPage === page ? 'active' : ''} key={page}>{page + 1}</a>)}


                <a onClick={() => getAllProducts(currentPage + 1)} className={data.length === currentPage + 1 ? 'disabled' : ''}>&raquo;</a>
            </div>            
            }

        </main>
    )
}

export default ProductList
